package patterns;

public class StarPattern6 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		// TODO Auto-generated method stub
	for(char ch='e';ch>='a';ch--)
	{
		for(char ch1='e';ch1>ch;ch1--)
		{
			System.out.print(" " );
		}
		for(char ch2='a';ch2<=ch;ch2++)
		{
			System.out.print(" "+ch2);
			
		}
		System.out.println();
	}
	}

}
